INSERT INTO `evaluasi` (`eva_id`, `kd_mk`, `kd_dosen`, `kd_prodi`, `pertemuan`, `jenis`, `tujuan`, `soal`, `a`, `b`, `c`, `d`, `e`, `kunci`) VALUES
(638, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '1', '<p>Kabel yang mengantarkan cahaya melalui serat kaca,yang dapat terbuat dengan bahan plastik disebut dengan</p>', 'Kabel Coaxial', 'Fiber Optik', 'STP', 'UTP', 'Kabel Twisted Pair', 'b'),
(639, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '1', '<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p>A. Harganya relatif murah</p>\r\n<p>B. penggunaannya mudah di bajak</p>\r\n<p>C. Harganya cukup mahal</p>\r\n<p>D. Hampir tidak terpengaruh noise</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Dari data di atas manakah yang tidak&nbsp; merupakan kelebihan dan kelemahan dari kabel coaxial</p>', 'A dan B', 'A dan C', 'A dan D', 'B dan D', 'C saja', 'e'),
(640, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '1', '<p>Kabel jaringan yang paling cepat untuk transfer data adalah</p>', 'Coaxial', 'UTP ', 'Thin Coaxial', 'Thick Coaxial', 'Fiber Optic', 'e'),
(641, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '1', '<p>Berikut ini yang bukan merupakan jenis kabel untuk jaringan komputer adalah</p>', 'BNC ', 'Fiber optic ', 'Coaxial Cable ', 'UTP', 'STP', 'a'),
(642, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '1', '<p>Yang tidak termasuk dalam prinsip transmisi fiber optic adalah</p>', 'Multimode ', 'Single mode', 'Multimode graded index', 'Multimode step indeks', 'Unimode', 'e'),
(643, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '2', '<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p>A. Kabel coaxial</p>\r\n<p>B. Fiber optic</p>\r\n<p>C. Twisted pair</p>\r\n<p>D. Bluetooth</p>\r\n<p>E. Wifi</p>\r\n<p>F. Infrared.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Berdasarkan data diatas, yang merupakan media transmisi wire (guided) yaitu</p>', 'A, B, E', 'B,C,D', 'A,B,C', 'C,D,E', 'D,E,F', 'c'),
(644, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '2', '<p>Di bawah ini yang tidak termasuk media transmisi unguided adalah</p>', 'Wifi ', 'Bluetooth', 'Infra red', 'Twisted pair ', 'Modem', 'd'),
(645, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '2', '<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p>A. Kabel coaxial</p>\r\n<p>B. Fiber optic</p>\r\n<p>C. Twisted pair</p>\r\n<p>D. Bluetooth</p>\r\n<p>E. Wifi</p>\r\n<p>F. Infrared</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Berdasarkan data diatas, yang merupakan media transmisi unguided yaitu</p>', 'A,B,E', 'B,C,D', 'A,B,C', 'C,D,E', 'D,E,F', 'e'),
(646, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '2', '<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p>Perhatikan ciri-ciri berikut !</p>\r\n<p>a.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Dapat dipandu melalui secara fisik&nbsp;&nbsp;</p>\r\n<p>b.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Menggunakan jenis kabel</p>\r\n<p>c.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Media itu sendiri yang&nbsp; akan menentukan batasan dari transmisi</p>\r\n<p>d.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ditentukan oleh kualitas sinyal antena transmisi</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Yang tidak termasuk dalam ciri media guided adalah</p>', 'A dan B', 'B dan D', 'A dan C', 'B dan D', 'D saja', 'e'),
(647, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '2', '<p>Yang bukan merupakan kelebihan dari unguided media adalah</p>', 'Tidak terpaku dalam satu titik saja ', 'Instalasi mudah ', 'Interferensi dapat diminimalkan', 'Memerlukan perangkat pemancar ', 'Area cukup luas ', 'c'),
(648, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '3', '<p>Jenis transmisi wireless yang digunakan untuk antenna parabolic adalah</p>', 'Gelombang radio ', 'Gelombang microwave ', 'Satelit microwave ', 'Wifi', 'Wimax', 'c'),
(649, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '3', '<p>Jenis transmisi wireless yang digunakan untuk sistem VSAT adalah</p>', 'Gelombang radio ', 'Gelombang microwave ', 'Satelit microwave ', 'Wifi', 'Wimax', 'b'),
(650, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '3', '<p>Gelombang microwave dapat digunakan untuk hal-hal berikut kecuali</p>', 'Telekomunikasi jarak jauh', 'Transmisi suara dan televisi', 'Local networks', 'Local data transmission ', 'Local data distribution', 'd'),
(651, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '3', '<p>Sejumlah band frekuensi yang beroperasi ketika satelit akan mengorbit disebut</p>', 'Channel transponder ', 'Channel transporter ', 'Channel remover ', 'Channel relay ', 'Channel frame', 'b'),
(652, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '3', '<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p>Perhatikan karakteristik transmisi berikut ini !</p>\r\n<p>&Oslash; Dapat diterima oleh semua stasiun</p>\r\n<p>&Oslash; Frekuensi transmisi dan penerimaan berbeda</p>\r\n<p>&Oslash; Tipe transmisinya full-duplex</p>\r\n<p>&Oslash; Timbul delay sebesar 240 sampai 300 ms</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Karakteristik diatas merupakan karakteristik transmisi dari</p>', 'Gelombang radio ', 'Gelombang microwave ', 'Satelit microwave ', 'Wifi', 'Wimax', 'c'),
(653, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '4', '<p>Jenis progasi di bawah ini yang termasuk dalam propagasi gelombang langit yaitu</p>', 'Propagasi Eksosfer', 'Propagasi Termosfer', 'Propagasi Mesosfer', 'Propagasi Stratosfer', 'Propagasi Troposfer', 'e'),
(654, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '4', '<p>Jangkauan dari propagasi ionosfir yaitu</p>', 'Antar benua', 'Antar kota', 'Antar gedung', 'Antar ruangan', 'Antar negara', 'a'),
(655, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '4', '<p>Propagasi garis pandang disebut juga sebagai</p>', 'Propagasi ground wave', 'Propagasi gelombang tanah ', 'Propagasi troposfir ', 'Propagasi ionosfir ', 'Propagasi langsung', 'e'),
(656, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '4', '<p>Band ELF digunakan pada</p>', 'Propagasi ground wave', 'Propagasi line of sight  ', 'Propagasi troposfir ', 'Propagasi ionosfir ', 'Propagasi langsung', ''),
(657, 'MK001', 'DS001', 'PTIN', '2', 'prepos', '4', '<p>Proses dimana dua antena pengarah diarahkan sedemikian rupa sehingga tembakan keduanya bertemu di troposfir disebut sebagai</p>', 'Line of sight ', 'Scaterring', 'Space wave ', 'Direct broadcast ', 'Aperture terminal', 'b');
